sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue == '') {
		return;
	}

	var whatNeeded = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');

	if(newValue == 'hr') {
		g_form.addOption('u_what_needed','hr11','Human Resources 11');
		g_form.addOption('u_what_needed','hr22','Human Resources 22');
		g_form.addOption('u_what_needed','hr33','Human Resources 33');
	}

	if(newValue == 'facilities') {
		g_form.addOption('u_what_needed','facilities11','facilities 11');
		g_form.addOption('u_what_needed','facilities22','facilities 22');
		g_form.addOption('u_what_needed','other','other');
	}

	if(newValue == 'legal'){
		g_form.addOption('u_what_needed','legal11','Legal 11');
		g_form.addOption('u_what_needed','legal22','Legal 22');
		g_form.addOption('u_what_needed','other','Other');
	}

	if(isLoading && !g_form.isNewRecord()){ //if form is loading and it is not new record 
		g_form.setValue('u_what_needed', whatNeeded);
	}
}
